import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Purchase from "../assets/icon/purchase.svg";
import { Link } from "react-router-dom";
import '../css/productIcon.css';

const ProductIcon = () => {
  const navigate = useNavigate();
  const [isFixed, setIsFixed] = useState(false);

  const handlePurchaseClick = () => {
    navigate("/product-registration"); // 이동 경로 수정
  };

  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      // 원하는 위치 (예: 100px)에 도달하면 고정
      setIsFixed(offset >= window.innerHeight - 100);
    };

    // 스크롤 이벤트 리스너 등록
    window.addEventListener('scroll', handleScroll);

    // 컴포넌트 언마운트 시 이벤트 리스너 제거
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div style={{ position: 'fixed', bottom: '0', right: '0' }}>
      <footer className="picon">
        <Link to='/ProductRegistration'>
          <li onClick={handlePurchaseClick} style={{ marginLeft: '23px' }}>
            <img className=" w-12 h-12 ml-3" src={Purchase} alt="purchase" />
            <strong>상품 등록</strong>
          </li>
        </Link>
      </footer >
    </div>
  );
}

export default ProductIcon;
