import React from "react";
import SearchIcon from "../assets/icon/search.svg";
import PersonIcon from "../assets/icon/person.svg";
import ShopCartIcon from "../assets/icon/shopcart.svg";
import WishlistIcon from "../assets/icon/wishlist.svg";
import Ai from "../assets/icon/ai.svg";
import '../css/banner.css';
import { useNavigate } from "react-router-dom";

export default function Banner() {
  const navigate = useNavigate();
  return (
    <div className="flex items-center pb-5 float-right w-4/5 justify-between">

      {/* 검색창 */}
      <div className="search-container">
        <input type="text" className="search-box" />
        <button className="search-button">
          <img src={SearchIcon} alt="search" />
        </button>
      </div>

      {/* 로그인, 장바구니, 위시리스트, ai추천 */}

      <ul className="mymenu">
        <li className="l1" onclick="window.location.href='Login.html'">
          <img className=" w-10 h-10" src={PersonIcon} alt="person" />
          <strong>로그인</strong>
        </li>

        <li className="l2" onclick="window.location.href='Cart.html'" style={'margin-left:23px;'}>
          <img className=" w-10 h-10" src={ShopCartIcon} alt="shopcart" />
          <strong>장바구니</strong>
        </li>

        <li className="l3" onclick="window.location.href='Wishlist.html'" style={'margin-left:23px;'}>
          <img className=" w-10 h-10" src={WishlistIcon} alt="wishlist" />
          <strong>좋아요</strong>
        </li>

        <li className="l4" onclick="window.location.href='AI.html'" style={'margin-left:23px;'}>
          <img className=" w-10 h-10" src={Ai} alt="ai" />
          <strong>AI추천</strong>
        </li >
      </ul >
    </div >
  );
}
